                    'anyOf(anyOf(string,PartitionsResponse),null)'
                );
        }
        if (isset($this->description)) {
            $json['description']              = $this->description;
        }
        if (isset($this->schedule)) {
            $json['schedule']                 = BillingScheduleEnum::checkValue($this->schedule);
        }
        if (isset($this->scheduleFactor)) {
            $json['scheduleFactor']           = $this->scheduleFactor;
        }
        if (isset($this->start)) {
            $json['start']                    = $this->start;
        }
        if (isset($this->finish)) {
            $json['finish']                   = $this->finish;
        }
        if (isset($this->collectionFactor)) {
            $json['collectionFactor']         = BillingsCollectionFactorEnum::checkValue($this->collectionFactor);
        }
        if (isset($this->collectionOffset)) {
            $json['collectionOffset']         = $this->collectionOffset;
        }
        if (isset($this->collectionIncludeCurrent)) {
            $json['collectionIncludeCurrent'] =
                BillingsCollectionIncludeCurrentEnum::checkValue(
                    $this->collectionIncludeCurrent
                );
        }
        if (!empty($this->currency)) {
            $json['currency']                 = CurrencyEnum::checkValue($this->currency['value']);
        }
        if (isset($this->inactive)) {
            $json['inactive']                 = InactiveEnum::checkValue($this->inactive);
        }
        if (isset($this->frozen)) {
            $json['frozen']                   = FrozenEnum::checkValue($this->frozen);
        }
        if (isset($this->collection)) {
            $json['collection']               = BillingCollectionEnum::checkValue($this->collection);
        }
        if (isset($this->billingEvents)) {
            $json['billingEvents']            = $this->billingEvents;
        }
        if (isset($this->billingModifiers)) {
            $json['billingModifiers']         = $this->billingModifiers;
        }
        if (isset($this->payoutFlows)) {
            $json['payoutFlows']              = $this->payoutFlows;
        }
        if (isset($this->payouts)) {
            $json['payouts']                  = $this->payouts;
        }
        if (isset($this->plans)) {
            $json['plans']                    = $this->plans;
        }
        if (isset($this->statementEntries)) {
            $json['statementEntries']         = $this->statementEntries;
        }
        if (isset($this->statements)) {
            $json['statements']               = $this->statements;
        }

        return (!$asArrayWhenEmpty && empty($json)) ? new stdClass() : $json;
    }
}
